(function() {
    'use strict';

    GroupController.$inject = ["$scope", "group", "_", "triTheming", "$timeout"];
    angular
        .module('app.manage')
        .controller('GroupController', GroupController);

    /* @ngInject */
    function GroupController($scope, group, _, triTheming, $timeout) {
        var vm = this;

        vm.group = null;
        vm.backup = angular.copy(group);

        //actions
        vm.toggleActive = toggleActive;
        vm.closeGroup = closeGroup;
        vm.revert = revert;
        vm.save = save;


        init();
        /////////////////////////////////

        function init() {
            vm.group = group;

            $scope.$emit('groupOpenend', vm.group);

            loadStudents();

        }

        function toggleActive() {
            vm.group.active = !vm.group.active;
            vm.group.save().then(groupSaved);
        }

        function closeGroup() {
            $scope.$emit('closeGroup');
        }

        function revert() {
            vm.group = angular.copy(vm.backup);
            collapseOptions();
        }

        function save() {
            vm.backup = angular.copy(vm.group);
            vm.group.save().then(groupSaved);
        }

        function groupSaved(response) {
            $scope.$emit('groupSaved', vm.group);
            collapseOptions();
        }

        function collapseOptions() {
            $scope.$broadcast('closeItems');
        }

        function loadStudents() {
            group.all('students').getList()
                .then(function(response) {
                    vm.students = response;
                });
        }


    }
})();

(function() {
    'use strict';

    GroupListController.$inject = ["$scope", "$filter", "$location", "$state", "$cacheFactory", "$mdDialog", "groups", "GroupService"];
    angular
        .module('app.manage')
        .controller('GroupListController', GroupListController);

    /* @ngInject */
    function GroupListController($scope, $filter, $location, $state, $cacheFactory, $mdDialog,
        groups, GroupService) {
        var vm = this;

        vm.baseState = 'triangular.manage.groups';
        vm.groups = [];

        //actions
        vm.openGroup = openGroup;
        vm.composeGroup = composeGroup;

        vm.selectedGroup = null;


        init();
        ////////////////////
        function init() {
            vm.groups = groups;
        }

        function openGroup(group) {
            $state.go(vm.baseState + '.group', {
                groupId: group.id
            });

        }

        function openlist() {
            $state.go(vm.baseState);
        }

        function composeGroup($event) {
            $mdDialog.show({
                    controller: 'ComposeGroupController',
                    controllerAs: 'vm',
                    templateUrl: 'app/manage/compose-group.tmpl.html',
                    targetEvent: $event,
                    locals: {
                        group: {
                            name: '',
                            active: true
                        }
                    },
                    getFocus: false
                })
                .then(saveGroup);
        }

        function saveGroup(group) {
            GroupService.post(group)
                .then(insertGroup);
        }

        function insertGroup(group) {
            var foundGroup = false;
            var i = 0;
            for (i = 0; i < vm.groups.length; i++) {
                if (vm.groups[i].id == group.id) {
                    vm.groups.splice(i, 1, group);
                    foundGroup = true;
                }
            }
            if (!foundGroup) {
                vm.groups.push(group);
            }
        }
        $scope.$on('groupOpenend', function($event, group) {
            vm.selectedGroup = group.id;
        });
        $scope.$on('groupSaved', function($event, group) {
            insertGroup(group);
        });
        $scope.$on('closeGroup', function() {
            vm.selectedGroup = null;
            openlist();
        });
        
    }
})();

