(function() {
    'use strict';

    UserService.$inject = ["$q", "$http", "$mdToast", "$rootScope", "Restangular", "_", "PermRoleStore", "PermPermissionStore", "GroupService", "StaffService"];
    angular
        .module('app.permission')
        .factory('UserService', UserService);

    /* @ngInject */
    function UserService($q, $http, $mdToast, $rootScope, Restangular, _,
        PermRoleStore, PermPermissionStore,
        GroupService, StaffService) {

        var currentUser = {};
        var activeGroup = null;
        var groups;

        var viewAllGroups = ['SUPERADMIN', 'ADMIN', 'CARE', 'CAREMANAGER', 'SECRETARY', 'MANAGER'];


        var service = {
            getCurrentUser: getCurrentUser,
            hasAllGroupsPermission: hasAllGroupsPermission,
            hasPermission: hasPermission,
            login: login,
            logout: logout,
            allowedGroups: allowedGroups,
            getActiveGroup: getActiveGroup,
            setActiveGroup: setActiveGroup
        };

        return service;

        ///////////////

        function getCurrentUser() {
            return currentUser;
        }

        function getRoles(user) {
            return Restangular.all('staff/login').post(user)
                .then(function(response) {
                    user.auth_token = response.auth_token;
                    user.given_name = response.given_name;
                    user.roles = [];
                    angular.forEach(response.roles, function(role) {
                        user.roles.push(role.name);
                    });

                    return user;
                }, function(err) {
                    $mdToast.show(
                        $mdToast.simple()
                        .content(err.data)
                        .position('bottom right')
                        .hideDelay(3000)
                    );
                });
        }

        function hasAllGroupsPermission(role) {
            return _.intersection(getCurrentUser().roles, viewAllGroups).length > 0;
        }

        function hasPermission(permission) {
            var deferred = $q.defer();
            var hasPermission = false;

            // check if user has permission via its roles
            angular.forEach(currentUser.roles, function(role) {
                // check role exists
                if (PermRoleStore.hasRoleDefinition(role)) {
                    // get the role
                    var roles = PermRoleStore.getStore();

                    if (angular.isDefined(roles[role])) {
                        // check if the permission we are validating is in this role's permissions
                        if (-1 !== roles[role].validationFunction[2](permission)) {
                            hasPermission = true;
                        }
                    }
                }
            });

            // if we have permission resolve otherwise reject the promise
            if (hasPermission) {
                deferred.resolve();
            } else {
                deferred.reject();
            }

            // return promise
            return deferred.promise;
        }

        function login(userinfo) {
            var deferred = $q.defer();

            getRoles(userinfo).then(function(user) {
                currentUser = user;
                deferred.resolve(currentUser);
            });

            return deferred.promise;

        }

        function logout() {
            PermPermissionStore.clearStore();
            PermRoleStore.clearStore();
        }


        function allowedGroups() {
            var user = getCurrentUser();

            var defer = $q.defer();

            if (groups == null) {
                //TODO: for now group selection only for staff users
                if (!hasAllGroupsPermission()) {
                    StaffService.one(user.auth_token).all('groups').getList().then(function(response) {
                        var mappedGroups = _.map(response, function(sig) {
                            return sig.group;
                        });
                        groups = mappedGroups;
                        defer.resolve(groups);
                    });

                } else {
                    GroupService.getList({ active: true }).then(function(response) {
                        groups = response;
                        defer.resolve(response);
                    });
                }
            } else {
                defer.resolve(groups);
            }

            return defer.promise;

        }

        function getActiveGroup() {
            var defer = $q.defer();

            if (activeGroup === null) {
                allowedGroups().then(function(response) {
                    if (response[0].isRestangularized()) {
                        activeGroup = response[0];
                        defer.resolve(activeGroup);
                    } else {
                        //TODO check if response[0] exists and ID exists on this item
                        GroupService.one(response[0].id).get()
                            .then(function(restGroup) {
                                activeGroup = restGroup;
                                defer.resolve(activeGroup);
                            });
                    }

                });
            } else {
                if (activeGroup.isRestangularized()) {
                    defer.resolve(activeGroup);
                } else {
                    GroupService.one(activeGroup.id).get()
                        .then(function(restGroup) {
                            activeGroup = restGroup;
                            defer.resolve(activeGroup);
                        });
                }
            }
            return defer.promise;
        }

        function setActiveGroup(group) {
            activeGroup = group;
            $rootScope.$broadcast('activeGroupChanged', group);
        }
    }
})();

(function() {
    'use strict';

    permissionRun.$inject = ["$rootScope", "$cookies", "$state", "PermPermissionStore", "PermRoleStore", "UserService", "_"];
    angular
        .module('app.permission')
        .run(permissionRun);

    /* @ngInject */
    function permissionRun($rootScope, $cookies, $state, PermPermissionStore, PermRoleStore, UserService, _) {


        // create permissions and add check function verify all permissions
        var permissions = [
            'dashboards',
            'care',
            'reporting', 'reportingEvaluations', 'reportingIAC', 'reportingReports',
            'manage', 'manageStudents', 'manageGroups', 'manageStaff',
            'addSunflowerEvaluation', 'addMultipleEvaluation'
        ];


        PermPermissionStore.defineManyPermissions(permissions, function(permissionName) {
            return UserService.hasPermission(permissionName);
        });

        // create roles for app
        PermRoleStore.defineManyRoles({
            'SUPERADMIN': preparePermissions(permissions), //ALLOW ALL FOR SA
            'ADMIN': preparePermissions(['dashboards', 'reporting', 'reportingEvaluations', 'reportingIAC', 'reportingReports', 'manage', 'manageStudents', 'manageGroups', 'manageStaff']),
            'TEACHER': preparePermissions(['dashboards', 'reporting', 'reportingEvaluations', 'reportingIAC', 'reportingReports']),
            'SECRETARY': preparePermissions(['dashboards', 'manage', 'manageStudents', 'manageGroups', 'manageStaff']),
            'MANAGER': preparePermissions(['dashboards', 'care', 'reporting', 'reportingEvaluations', 'reportingIAC', 'reportingReports']),
            'CAREMANAGER': preparePermissions(['dashboards', 'care', 'reporting', 'reportingEvaluations']),
            'CARE': preparePermissions(['dashboards', 'care', 'reporting', 'reportingEvaluations']),
            'ANONYMOUS': preparePermissions([])
        });

        function preparePermissions(perms) {
            return function(perm) {
                _.includes(perms, perm);
            }
        };
        ///////////////////////

        // default redirect if access is denied
        function accessDenied() {
            $state.go('authentication.login');
        }

        // watches

        // redirect all denied permissions to 401
        var deniedHandle = $rootScope.$on('$stateChangePermissionDenied', accessDenied);

        // remove watch on destroy
        $rootScope.$on('$destroy', function() {
            deniedHandle();
        });
    }
})();

(function() {
    'use strict';
    GoogleService.$inject = ["$http", "$rootScope", "$q", "UserService", "_env"];
    angular
        .module('app.permission')
        .factory('googleService', GoogleService);

    /* @ngInject */
    function GoogleService($http, $rootScope, $q, UserService, _env) {
        var deferred = $q.defer();

        var service = {
            login: login,
            handleClientLoad: handleClientLoad,
            checkAuth: checkAuth,
            handleAuthResult: handleAuthResult,
            handleAuthClick: handleAuthClick
        }
        return service;

        //////////////////////


        function login() {
            gapi.auth.authorize({
                client_id: _env.googleClientId,
                scope: _env.scopes,
                immediate: false,
                hd: _env.domain
            }, handleAuthResult);

            return deferred.promise;
        }

        function handleClientLoad() {
            gapi.client.setApiKey(_env.googleSecret);
            gapi.auth.init(function() {});
            window.setTimeout(checkAuth, 1);
        };

        function checkAuth() {
            gapi.auth.authorize({
                client_id: _env.googleClientId,
                scope: _env.scopes,
                immediate: false,
                hd: _env.domain
            }, handleAuthResult);
        };

        function handleAuthResult(authResult) {
            if (authResult && !authResult.error) {
                var data = {};

                gapi.client.load('oauth2', 'v2', function() {
                    var request = gapi.client.oauth2.userinfo.get();
                    request.execute(function(resp) {
                        data = resp;
                        data.provider = 'google+';

                        UserService.login(data)
                            .then(function(user) {
                                deferred.resolve(user);
                            });

                    });
                });
            } else {
                //$rootScope.$broadcast(AUTH_EVENTS.loginFailed);
                deferred.reject('error');
            }
        };

        function handleAuthClick(event) {
            gapi.auth.authorize({
                client_id: _env.googleClientId,
                scope: _env.scopes,
                immediate: false,
                hd: _env.domain
            }, handleAuthResult);
            return false;
        };
    }
})();

